# Configuración de la página de Streamlit
st.title("SISTEMA DE INFORMACIÓN Y SEGURIDAD CIUDADANA DEL MUNICIPIO DE SAN LUIS ANTIOQUIA")

# Configuración de la cuadrícula
num_filas = 4
num_columnas = 3
graficos_por_fila = len(info_graficos) // num_filas

# Crear la cuadrícula
for i in range(num_filas):
    for j in range(num_columnas):
        idx = i * num_columnas + j
        if idx < len(info_graficos):
            info = info_graficos[idx]
            url, titulo, etiqueta_x, etiqueta_y = info['url'], info['titulo'], info['etiqueta_x'], info['etiqueta_y']

            st.subheader(titulo)
            
            df = pd.read_csv(url)
            df['fecha_hecho'] = pd.to_datetime(df['fecha_hecho'])
            df['fecha_hecho'] = df['fecha_hecho'].dt.year
            cantidad_por_año = df.groupby('fecha_hecho')['cantidad'].sum().reset_index()

            # Crear gráfico interactivo con Altair
            chart = alt.Chart(cantidad_por_año).mark_bar().encode(
                x=etiqueta_x,
                y=etiqueta_y,
                tooltip=[etiqueta_x, etiqueta_y]
            ).interactive()

            st.altair_chart(chart, use_container_width=True)
